'use strict';

const Parser = require('postcss-values-parser/lib/parser');
const AtWord = require('postcss-values-parser/lib/atword');
const Colon = require('postcss-values-parser/lib/colon');
const Comma = require('postcss-values-parser/lib/comma');
const Comment = require('postcss-values-parser/lib/comment');
const Func = require('postcss-values-parser/lib/function');
const Num = require('postcss-values-parser/lib/number');
const Operator = require('postcss-values-parser/lib/operator');
const Paren = require('postcss-values-parser/lib/paren');
const Str = require('postcss-values-parser/lib/string');
const UnicodeRange = require('postcss-values-parser/lib/unicode-range');
const Value = require('postcss-values-parser/lib/value');
const Word = require('postcss-values-parser/lib/word');

let parser = function (source, options) {
  return new Parser(source, options);
};

parser.atword = function (opts) {
  return new AtWord(opts);
};

parser.colon = function (opts) {
  return new Colon(Object.assign({ value: ':' }, opts));
};

parser.comma = function (opts) {
  return new Comma(Object.assign({ value: ',' }, opts));
};

parser.comment = function (opts) {
  return new Comment(opts);
};

parser.func = function (opts) {
  return new Func(opts);
};

parser.number = function (opts) {
  return new Num(opts);
};

parser.operator = function (opts) {
  return new Operator(opts);
};

parser.paren = function (opts) {
  return new Paren(Object.assign({ value: '(' }, opts));
};

parser.string = function (opts) {
  return new Str(Object.assign({ quote: '\'' }, opts));
};

parser.value = function (opts) {
  return new Value(opts);
};

parser.word = function (opts) {
  return new Word(opts);
};

parser.unicodeRange = function (opts) {
  return new UnicodeRange(opts);
};

module.exports = parser;
